parameters.inputSchema:
{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Generated Viral Tweet Options",
  "description": "A structured output containing the best three distinct tweet variations for an n8n automation promotion.",
  "type": "object",
  "required": [
    "tweet_options"
  ],
  "properties": {
    "tweet_options": {
      "description": "An array containing the three generated tweet objects.",
      "type": "array",
      "minItems": 3,
      "maxItems": 3,
      "items": {
        "type": "object",
        "required": [
          "analysis",
          "tweet_text"
        ],
        "properties": {
          "analysis": {
            "description": "The LLM's explanation of which example structure it applied to generate this tweet.",
            "type": "string"
          },
          "tweet_text": {
            "description": "The full, ready-to-post text of the tweet, including line breaks.",
            "type": "string"
          }
        }
      }
    }
  }
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Generated LinkedIn Post Options",
  "description": "A structured output containing three distinct LinkedIn post variations designed to drive multi-platform growth (LinkedIn, YouTube, Skool).",
  "type": "object",
  "required": [
    "post_options"
  ],
  "properties": {
    "post_options": {
      "description": "An array containing the three generated LinkedIn post objects.",
      "type": "array",
      "minItems": 3,
      "maxItems": 3,
      "items": {
        "type": "object",
        "required": [
          "analysis",
          "post_text"
        ],
        "properties": {
          "analysis": {
            "description": "A brief explanation of which LinkedIn example structure was applied to generate this post.",
            "type": "string"
          },
          "post_text": {
            "description": "The full, ready-to-post text of the LinkedIn post, including necessary line breaks for readability.",
            "type": "string"
          }
        }
      }
    }
  }
}
